def find_mid_edge(v, w, top, bottom, left, right, match, mu, sigma):

    n = bottom - top
    m = right + left
    middle = m // 2
    from_source = [[0]*(n+1), [0]*(n+1)]
    curr_col = 0

    for i in range(1, n+1):
        from_source[curr_col][i] = from_source[curr_col][i-1] - sigma # moving down indels
    curr_col = 1 - curr_col
    for j_char in range(left, left + middle + 2):
        if j_char > bottom-1:
            continue
        from_source[curr_col][0] = from_source[1-curr_col][0] - sigma # moving right indels
        for i in range(1, n+1):
            i_char = i + top - 1
            s_1 = from_source[curr_col][i-1] - sigma # move down
            s_2 = from_source[1-curr_col][i] - sigma # move right
            s_3 = from_source[1-curr_col][i-1] + match if v[i_char] == w[j_char] else from_source[1-curr_col][i-1] - mu # diagonally
            from_source[curr_col][i] = max(s_1, s_2, s_3)
        curr_col = 1 - curr_col
    middle_col_1 = curr_col

    to_sink = [[0]*(n+1), [0]*(n+1)]
    curr_col = 0
    for i in range(n-1, -1, -1): # from bottom to top
        to_sink[curr_col][i] = to_sink[curr_col][i+1] - sigma
        curr_col = 1 - curr_col
        for j_char in range(right-1, left+middle-1, -1): # from right to middle
            to_sink[curr_col][n] = to_sink[1-curr_col][n] - sigma
            for i in range(n-1, -1, -1): # from bottom to top
                i_char = i + top - 1
                s_1 = to_sink[curr_col][i+1] - sigma # move up
                s_2 = to_sink[1-curr_col][i] - sigma # move left
                s_3 = to_sink[1-curr_col][i+1] + match if v[i_char] == w[j_char] else from_source[1-curr_col][i-1] - mu # move diagonally
                to_sink[curr_col][i] = max(s_1, s_2, s_3)
            curr_col = 1 - curr_col
        middle_col_2 = 1 - curr_col

        length = [0]*(n+1)
        for i in range(n+1):
            length[i] = from_source[middle_col_1][i] + to_sink[middle_col_2][i] # length of all longest middle paths
        i_max = max(range(len(length)), key = lambda x: length[x])
        i_1 = top + i_max - 1
        j_1 = left + middle
        if i_max == n:
            i_2 = i_1
            j_2 = j_1 + 1
        else:
            s_1 = from_source[1-middle_col_1][i_max] + to_sink[1-middle_col_2][i_max]
            s_2 = from_source[middle_col_1][i_max+1] + to_sink[middle_col_2][i_max+1]
            s_3 = from_source[1-middle_col_1][i_max+1] + to_sink[1-middle_col_2][i_max+1]
            s_max = max(s_1, s_2, s_3)
            if s_max == s_3:
                i_2 = i_1 + 1
                j_2 = j_1 + 1
            elif s_max == s_2:
                i_2 = i_1 + 1
                j_2 = j_1
            elif s_max == s_1:
                i_2 = i_1
                j_2 = j_1 + 1

    print('Middle Node Coordinates: ' + str((i_1, j_1)))
    print('Coordinates of Next Node in Alignment: ' + str((i_2, j_2)))



