def mult_align_backtrack(u, v, w): # v = vertical, w = horizontal

    n = len(u)
    m = len(v)
    l = len(w)

    s = [[[0 for i in range(l+1)] for j in range(m+1)] for k in range(n+1)]
    backtrack = [[[0 for i in range(l+1)] for j in range(m+1)] for k in range(n+1)]
    score = [0 for i in range(8)]

    for i in range(1, n+1):
        backtrack[i][0][0] = 1
    for j in range(1, m + 1):
        backtrack[0][j][0] = 2
    for k in range(1, l + 1):
        backtrack[0][0][k] = 3
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            backtrack[i][j][0] = 4
        for k in range(1, l + 1):
            backtrack[i][0][k] = 5
    for j in range(1, m + 1):
        for k in range(1, l + 1):
            backtrack[0][j][k] = 6
            # for i in range(1, n+1):
            #     backtrack[i][j][k] = 0
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            for k in range(1, l + 1):
                score = [s[i - 1][j - 1][k - 1] + 1 if u[i - 1] == v[j - 1] == w[k - 1] else s[i - 1][j - 1][k - 1], s[i - 1][j][k], s[i][j - 1][k], s[i][j][k - 1], s[i - 1][j - 1][k], s[i - 1][j][k - 1], s[i][j - 1][k - 1]]
                index = np.argmax(score)
                backtrack[i][j][k] = index
                s[i][j][k] = score[index]

    print('Multiple Alignment Score: ' + str(s[n][m][l]))
    return backtrack




def mult_align_lcs(u, v, w, i, j, k): # v = vertical, w = horizontal

    backtrack = mult_align_backtrack(u, v, w)

    lcs_u = ''
    lcs_v = ''
    lcs_w = ''

    while i > 0 or j > 0 or k > 0:

        if backtrack[i][j][k] == 1:
            i -= 1
            lcs_u = u[i] + lcs_u
            lcs_v = '-' + lcs_v
            lcs_w = '-' + lcs_w
            continue
        elif backtrack[i][j][k] == 2:
            j -= 1
            lcs_u = '-' + lcs_u
            lcs_v = v[j] + lcs_v
            lcs_w = '-' + lcs_w
            continue
        elif backtrack[i][j][k] == 3:
            k -= 1
            lcs_u = '-' + lcs_u
            lcs_v = '-' + lcs_v
            lcs_w = w[k] + lcs_w
            continue
        elif backtrack[i][j][k] == 4:
            i -= 1
            j -= 1
            lcs_u = u[i] + lcs_u
            lcs_v = v[j] + lcs_v
            lcs_w = '-' + lcs_w
            continue
        elif backtrack[i][j][k] == 5:
            i -= 1
            k -= 1
            lcs_u = u[i] + lcs_u
            lcs_v = '-' + lcs_v
            lcs_w = w[k] + lcs_w
            continue
        elif backtrack[i][j][k] == 6:
            j -= 1
            k -= 1
            lcs_u = '-' + lcs_u
            lcs_v = v[j] + lcs_v
            lcs_w = w[k] + lcs_w
            continue
        elif backtrack[i][j][k] == 0:
            i -= 1
            j -= 1
            k -= 1
            lcs_u = u[i] + lcs_u
            lcs_v = v[j] + lcs_v
            lcs_w = w[k] + lcs_w
            continue

    print(lcs_u)
    print(lcs_v)
    print(lcs_w)



